import React, { useState } from 'react'
import s from './Content.module.css'

function Content(props) {

    let copy = [...props.SelectedItem];
    copy[props.Id] = 0;

    let StartElements = [[],[],[],[],[],[],[],[]];
    let StartElementsMod = [[],[],[],[],[],[],[],[]];
    let VisiualElements = [[],[],[],[],[],[],[],[]];

    for(let i = 0; i < 8; i++) {
        for(let j = 0; j < 8; j++) {
            StartElementsMod[i][j] = 0;
        }
    }


    function UpdateField () {
    for(let i = 0; i < 8; i++) {
        for(let j = 0; j < 8; j++) {
            let isBomb = Math.floor(Math.random() * 50);;

            if((isBomb < 15) && (StartElements[i][j] != 0)) {
                StartElements[i][j] = -1;
            }
            else {
                StartElements[i][j] = 0;
            }
        }
    }

    for(let i = 0; i < 8; i++) {
        for(let j = 0; j < 8; j++) {
            if(StartElements[i][j] !== -1) {
                let count = 0;
                for(let k = i - 1; k <= i + 1; k++) {
                    for(let f = j - 1; f <= j + 1; f++) {
                        try {
                            if(StartElements[k][f] === -1) {
                                count++;
                            }
                        }
                        catch {}
                    }
                }
                StartElements[i][j] = count;
            }
        }
        copy
    }

    }




    //-1 бомба
    //0-8 - цифра
    let [Elements, SetElements] = useState([[],[],[],[],[],[],[],[]]);
    let [ElementsMod, SetElementsMod] = useState([[],[],[],[],[],[],[],[]]);

    for(let i = 0; i < 8; i++) {
        let tempRow = 0;
        VisiualElements[i] = Elements[i].map((el) => {
            let colum = i;
            let row = tempRow;
            let style = Elements[colum][row];
            if (Elements[colum][row] === -1) style = "x"
            tempRow++;
            function click() {
                if(Elements[colum][row] === -1) {
                    let copy = [...ElementsMod, [...ElementsMod[colum]]]
                    copy[colum][row] = 1
                    SetElementsMod(copy)
                }
                if(Elements[colum][row] >= 0) {
                    let copy = [...ElementsMod, [...ElementsMod[colum]]]
                    copy[colum][row] = 1
                    SetElementsMod(copy)
                }
                else {
                    UpdateField()
                    let copy = [...ElementsMod, [...ElementsMod[colum]]]
                    copy[colum][row] = 1
                    SetElementsMod(copy)
                }
            }
            // 0 не трогал  
            // 1 значение открыто

            return <div onClick={() => { click()} } className={s.Elem}>{ElementsMod[colum][row] ? style : "?"}</div>
         })
    }

    return (
        <div className={s.Content}>
            <div>{VisiualElements[0]}</div>
            <div>{VisiualElements[1]}</div>
            <div>{VisiualElements[2]}</div>
            <div>{VisiualElements[3]}</div>
            <div>{VisiualElements[4]}</div>
            <div>{VisiualElements[5]}</div>
            <div>{VisiualElements[6]}</div>
            <div>{VisiualElements[7]}</div>
        </div>
    );
}


export default Content;
